import React from "react";
import './Testimonial.css';
import { Swiper, SwiperSlide } from "swiper/react";;
import profilePic1 from '../../img/profile1.jpg'
import profilePic2 from '../../img/profile2.jpg'
import profilePic3 from '../../img/profile3.jpg'
import profilePic4 from '../../img/profile4.jpg'

export const Testimonial = () => {
    const clients = [
        {
            img: profilePic1,
            review: "A good review should describe various aspects of the customer experience. Did the customer receive fantastic customer service? Was an employee particularly helpful, and did the customer leave the employee’s name in the review? Did the customer mention other elements of the customer experience, like a great website that was easy to use? These details will not only be appealing to prospects, but it will help you know what your business is doing well.",
        },
        {
            img: profilePic2,
            review: "A good review should describe various aspects of the customer experience. Did the customer receive fantastic customer service? Was an employee particularly helpful, and did the customer leave the employee’s name in the review? Did the customer mention other elements of the customer experience, like a great website that was easy to use? These details will not only be appealing to prospects, but it will help you know what your business is doing well.",
        },
        {
            img: profilePic3,
            review: "A good review should describe various aspects of the customer experience. Did the customer receive fantastic customer service? Was an employee particularly helpful, and did the customer leave the employee’s name in the review? Did the customer mention other elements of the customer experience, like a great website that was easy to use? These details will not only be appealing to prospects, but it will help you know what your business is doing well.",
        },
        {
            img: profilePic4,
            review: "A good review should describe various aspects of the customer experience. Did the customer receive fantastic customer service? Was an employee particularly helpful, and did the customer leave the employee’s name in the review? Did the customer mention other elements of the customer experience, like a great website that was easy to use? These details will not only be appealing to prospects, but it will help you know what your business is doing well.",
        }
    ]

    return(
        <div className="t-wrapper">
            <div className="t-heading">
                <span>Clients always get</span>
                <span>Exceptional Work</span> 
                <span>from me...</span>
                <div className="blur t-blur1" style={{background: "var(--purple)"}}></div>
                <div className="blur t-blur2" style={{background: "var(--skyblue)"}}></div>
            </div>

            {/* Slider */}
            <Swiper>
                {clients.map((clients,index))}
            </Swiper>
        </div>

    )
}
